const INITIAL_STATE = {
    midDisplayer: '',
    argumentos: '',
    operators: [],
    topDisplayer: ''
};

function reducer(state = INITIAL_STATE, action){
        switch(action.type){
        case 'ADD_OPERATION':
            return {argumentos: [...state.argumentos, state.topDisplayer, action.newOperator] ,midDisplayer: action.newOperator, operators: [...state.operators, action.newOperator]}
        case 'ADD_NUMBER':
            return{...state, midDisplayer: state.midDisplayer+action.newNumber, topDisplayer: state.topDisplayer + action.newNumber}
        case 'SHOW_RESULT':
            return{midDisplayer: action.result, argumentos: state.argumentos + '=' + action.result}
        case 'RESET':
            return INITIAL_STATE
        default:
            return state
    }
};


const OperationFns = {
    mapStateToProps: function(state){
        return {
            operators: state.operators
        }
    },

    mapDispatchToProps: function(dispatch){
        return {
            addOperator: (val) => dispatch({type: 'ADD_OPERATION', newOperator: val})
        }
    }

}

const ButtonsFns = {
    mapDispatchToProps: function(dispatch){
        return {
            addNumber: (val,op) => dispatch({type: 'ADD_NUMBER', newNumber: val, newArgumentos: op})
        }
    }

}


const EqualFns = {
    mapStateToProps: function(state){
        return {
            argumentos: state.argumentos,
            midDisplayer: state.midDisplayer
        }
    },
    mapDispatchToProps: function(dispatch){
        return {
            dispatchResult: (resultado) => dispatch({type: 'SHOW_RESULT', result: resultado})
        }
    }

}

const DisplayFns = {
    mapStateToProps: function(state){
        return {
            argumentos: state.argumentos,
            midDisplayer: state.midDisplayer,
            operators: state.operators,
            topDisplayer: state.topDisplayer
        }
    }
}



export { reducer, OperationFns, ButtonsFns, EqualFns, DisplayFns };
