const INITIAL_STATE = {
    midDisplayer: '',
    argumentos: [], 
    lastInput: ''
};

function reducer(state = INITIAL_STATE, action){
        switch(action.type){
        case 'ADD_OPERATION':
            switch(state.lastInput+action.newOperator){
                        case '--':
                        case 'x-':
                        case '/-':
                        case '+-':
                            return {argumentos : [...state.argumentos, state.midDisplayer+action.newOperator], midDisplayer: action.newOperator,lastInput: state.midDisplayer}
//                         case '-+':
//                         case '-/':
//                         case '-x':
                        default:
                            return {argumentos: () => {state.argumentos.pop();
                                console.log(state.argumentos)
                                return [...state.argumentos, action.newOperator]}
                                , midDisplayer: action.newOperator, lastInput: state.midDisplayer }
//                         default:
//                             return {}
                            }
        case 'ADD_NUMBER':
            return {argumentos: () => function(){
                let aux = (state.argumentos.length == 0) ? '' : state.argumentos.pop();
                return [...state.argumentos,aux+action.newNumber]
            } , midDisplayer: state.midDisplayer + action.newNumber, lastInput: state.midDisplayer[state.midDisplayer.length -1]}
        case 'SHOW_RESULT': 
            return {...state, topDisplayer: state.topDisplayer + action.result, midDisplayer: action.result}
        default:
            return state
    }
};


const OperationFns = {
    mapStateToProps: function(state){
        return {
            lastInput: state.lastInput
        }
    },

    mapDispatchToProps: function(dispatch){
        return {
            addOperator: (val) => dispatch({type: 'ADD_OPERATION', newOperator: val})
        }
    }

}

const ButtonsFns = {
    mapDispatchToProps: function(dispatch){
        return {
            addNumber: (val) => dispatch({type: 'ADD_NUMBER', newNumber: val})
        }
    }

}


const EqualFns = {
    mapStateToProps: function(state){
        return {
            argumentos: state.argumentos,
            midDisplayer: state.midDisplayer
        }
    },
    mapDispatchToProps: function(dispatch){
        return {
            dispatchResult: (resultado) => dispatch({type: 'SHOW_RESULT', result: resultado})
        }
    }

}

const DisplayFns = {
    mapStateToProps: function(state){
        return {
            argumentos: state.argumentos,
            midDisplayer: state.midDisplayer
        }
    }
}



export { reducer, OperationFns, ButtonsFns, EqualFns, DisplayFns };
