const INITIAL_STATE = {
    midDisplayer: '',
    argumentos: [],
    operators: '',
    numbers: ''
};

function reducer(state = INITIAL_STATE, action){
        switch(action.type){
        case 'ADD_OPERATION':
            return {midDisplayer: action.newOperator[action.newOperator.length -1], argumentos: (state.operators == '') ? [...state.argumentos, state.numbers] : [...state.argumentos], numbers: '', operators: action.newOperator}
        case 'ADD_NUMBER':
            return{midDisplayer: state.numbers+action.newNumber, argumentos: (state.numbers == '') ? [...state.argumentos, state.operators] : [...state.argumentos], numbers: state.numbers }
        case 'SHOW_RESULT':
            return{}
        case 'RESET':
            return INITIAL_STATE
        default:
            return state
    }
};


const OperationFns = {
    mapStateToProps: function(state){
        return {
            operators: state.operators
        }
    },

    mapDispatchToProps: function(dispatch){
        return {
            addOperator: (val) => dispatch({type: 'ADD_OPERATION', newOperator: val})
        }
    }

}

const ButtonsFns = {
    mapDispatchToProps: function(dispatch){
        return {
            addNumber: (val,op) => dispatch({type: 'ADD_NUMBER', newNumber: val, newArgumentos: op})
        }
    }

}


const EqualFns = {
    mapStateToProps: function(state){
        return {
            argumentos: state.argumentos,
            midDisplayer: state.midDisplayer
        }
    },
    mapDispatchToProps: function(dispatch){
        return {
            dispatchResult: (resultado) => dispatch({type: 'SHOW_RESULT', result: resultado})
        }
    }

}

const DisplayFns = {
    mapStateToProps: function(state){
        return {
            argumentos: state.argumentos,
            midDisplayer: state.midDisplayer,
            operators: state.operators,
            numbers: state.numbers
        }
    }
}



export { reducer, OperationFns, ButtonsFns, EqualFns, DisplayFns };
