INITIAL_STATE = {
    topDisplayer: '',
    midDisplayer: ''
};

function reducer(state = INITIAL_STATE, action){
        switch(action.type){
        case 'ADD_OPERATION':
            return {topDisplayer: state.topDisplayer + action.newOperator, midDisplayer: action.newOperator}
        case 'ADD_NUMBER':
            return {topDisplayer: state.topDisplayer + action.newNumber, midDisplayer: state.midDisplayer + action.newNumber}
        case 'SHOW_RESULT': 
            return {topDisplayer: state.topDisplayer + action.result, midDisplayer: action.result}
        default:
            return state
    }
};


const OperationFns = {
    mapDispatchToProps: function(dispatch){
        return {
            addOperator: (val) => dispatch({type: 'ADD_OPERATION', newOperator: val})
        }
    }

}

const ButtonsFns = {
    mapDispatchToProps: function(dispatch){
        return {
            addNumber: (val) => dispatch({type: 'ADD_NUMBER', newNumber: val})
        }
    }

}


const EqualFns = {
    mapStateToProps: function(state){
        return {
            topDisplayer: state.topDisplayer,
            midDisplayer: state.midDisplayer
        }
    },
    mapDispatchToProps: function(dispatch){
        return {
            dispatchResult: (resultado) => dispatch({type: 'SHOW_RESULT', result: resultado})
        }
    }

}

const DisplayFns = {
    mapStateToProps: function(state){
        return {
            topDisplayer: state.topDisplayer,
            midDisplayer: state.midDisplayer
        }
    }
}



export { reducer, OperationFns, ButtonsFns, EqualFns };
