import React from 'react';

class Displayer extends React.Component {
    constructor(props){
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.handleTime = this.handleTime.bind(this);
    }
    handleTime(){
        let timeAux = this.props.timeLeft.split(':').map((el) => parseInt(el));
            if (timeAux[1] > 0){
                timeAux[1] = 59;
                timeAux[0] -= 1;
            }
            if (timeAux[0] == 0 && timeAux[1] == 0){
                this.props.stst();
            }

        return timeAux.join(':')
    }

    handleClick(event){

        if (event.target.id == 'i1'){
            this.props.stst()
            if (this.props.paused){
                const anchor = setInterval(function(){
                    this.props.proc(this.handleTime())
                }, 1000);    
            }

        }
        else{

            this.props.reset();
//             clearInterval(anchor)
        }

    };
    render(){
        return <div>
            <div className={'border border-primary'}>
                <h4 id={'timer-label'}>{'Session'}</h4>
                <h2 id={'time-left'}>{this.props.timeLeft}</h2>
                <div className={'container-fluid row'}>
                    <div>
                        <button id={'start_stop'} onClick={this.handleClick}><i id={'i1'} className={'carousel-control-next-icon'}></i></button>
                    </div>
                    <div>
                        <button id={'reset'} onClick={this.handleClick}><i id={'i2'} className={'accordion-button'}></i></button>
                    </div>
                </div>
            </div>
        </div>
    }
};

export default Displayer;
