const INITIAL_STATE = {
    midDisplayer: '',
    argumentos: '' 
};

function reducer(state = INITIAL_STATE, action){
        switch(action.type){
        case 'ADD_OPERATION':
            return {midDisplayer: action.newOperator,argumentos: state.argumentos+action.newOperator}
        case 'ADD_NUMBER':
            return{midDisplayer: state.midDisplayer+action.newNumber, argumentos: state.argumentos+ action.newNumber}
        case 'REMOVE_LAST':
            return {midDisplayer: action.newOperator, argumentos: () => {state.argumentos.split().pop(); state.argumentos.push(action.newOperator)}}
        case 'SHOW_RESULT':
            return{midDisplayer: action.result, argumentos: state.argumentos + '=' + action.result}
        default:
            return state
    }
};


const OperationFns = {
    mapStateToProps: function(state){
        return {
            argumentos: state.argumentos
        }
    },

    mapDispatchToProps: function(dispatch){
        return {
            addOperator: (val) => dispatch({type: 'ADD_OPERATION', newOperator: val}),
            removeLastInput: (val) => dispatch({type: 'REMOVE_LAST', newOperator: val})
        }
    }

}

const ButtonsFns = {
    mapDispatchToProps: function(dispatch){
        return {
            addNumber: (val) => dispatch({type: 'ADD_NUMBER', newNumber: val})
        }
    }

}


const EqualFns = {
    mapStateToProps: function(state){
        return {
            argumentos: state.argumentos,
            midDisplayer: state.midDisplayer
        }
    },
    mapDispatchToProps: function(dispatch){
        return {
            dispatchResult: (resultado) => dispatch({type: 'SHOW_RESULT', result: resultado})
        }
    }

}

const DisplayFns = {
    mapStateToProps: function(state){
        return {
            argumentos: state.argumentos,
            midDisplayer: state.midDisplayer
        }
    }
}



export { reducer, OperationFns, ButtonsFns, EqualFns, DisplayFns };
