import React from 'react';

class Displayer extends React.Component {
    constructor(props){
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.handleTime = this.handleTime.bind(this);
    }
    handleTime(){
        let timeAux = [...this.props.timeLeft];
            if (timeAux[1] > 0){
                timeAux[1] = 59;
                timeAux[0] -= 1;
            }
            if (timeAux[0] == 0 && timeAux[1] == 0){
                this.props.changeTimer()
                console.log('Times up')
            }
        return timeAux
    }

    handleClick(event){
        if (event.target.id == 'i1'){

                const anchor = setInterval(this.props.ticToc(this.handleTime()), 1000);    
        }
        else{
            this.props.reset();
        }
        this.props.stst()

    };
    render(){
        return <div>
            <div className={'border border-primary'}>
                <h4 id={'timer-label'}>{this.props.timer}</h4>
                <h2 id={'time-left'}>{this.props.timeLeft.map((el) => el.toString()).join(':')}</h2>
                <div className={'container-fluid row'}>
                    <div>
                        <button id={'start_stop'} onClick={this.handleClick}><i id={'i1'} className={'carousel-control-next-icon'}></i></button>
                    </div>
                    <div>
                        <button id={'reset'} onClick={this.handleClick}><i id={'i2'} className={'accordion-button'}>{'RESET'}</i></button>
                    </div>
                </div>
            </div>
        </div>
    }
};

export default Displayer;
