const INITIAL_STATE = {
    breakLength: 5,
    sessionLength: 25,
    timeLeft: '25:00',
    paused: true
};
const MAGNITUDE = 1;
const VELOCITY = 1;

function reducer(state = INITIAL_STATE, action){
        switch(action.type){
            case 'INCREMENT_SESSION':
                return {...state, sessionLength: state.sessionLength+MAGNITUDE}
            case 'INCREMENT_BREAK':
                return {...state, breakLength: state.breakLength+MAGNITUDE}
            case 'DECREMENT_SESSION':
                return {...state, sessionLength: state.sessionLength-MAGNITUDE}
            case 'DECREMENT_BREAK':
                return {...state, breakLength: state.breakLength-MAGNITUDE}
            case 'PROC':
                return {...state, timeLeft: action.passTime}
            case 'START':
                return {...state, paused: state.paused ? false : true}
            case 'RESET':
                return INITIAL_STATE
        default:
            return state
        }
};

const breakMap = {
    mapStateToProps: function(state){
        return {
            breakLength: state.breakLength
        }
    },
    mapDispatchToProps: function(dispatch){
    return{
        inc : () => dispatch({type: 'INCREMENT_BREAK'}),
        dec: () => dispatch({type: 'DECREMENT_BREAK'})
    }
    }
}
const sessionMap = {
    mapStateToProps: function(state){
        return {
            sessionLength: state.sessionLength
        }
    },
    mapDispatchToProps: function(dispatch){
        return {
        inc : () => dispatch({type: 'INCREMENT_SESSION'}),
        dec: () => dispatch({type: 'DECREMENT_SESSION'})
        }
        }
}

const displayerMap = {
    mapStateToProps: function(state){
        return {
            breakLength: state.breakLength,
            sessionLength: state.sessionLenght,
            timeLeft: state.timeLeft
        }
    },
    mapDispatchToProps: function(dispatch){
        return {
            proc: () => dispatch({type: 'PROC', timeProc: VELOCITY})
        }
    }
}



export { reducer, displayerMap, sessionMap, breakMap };
